// Anonymous
var movie = function(name){
    return "The movie is " + name;
}


function addMe(num1, num2, sumAndShow, sumFor2){
    var sum = num1 + num2;

    sumAndShow(sum);
    sumFor2(sum);

    return sum;
}

// Callback
addMe(5, 5, function(data){
    console.log("The sum is: ", data);
},
    function(data){
        console.log("The sum for 2 is: ", (data*2));
    }    
);
